import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import BuildIcon from '@material-ui/icons/Build';
import ComputerIcon from '@material-ui/icons/Computer';
const useStyles = makeStyles((theme) => ({
  root: {
    width: 345,
    // backgroundColor: '#30475e',
    borderRadius: 6,

    margin: 24
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
    padding: 24,
    transition: '0.2s',
    '&:hover': {
      transform: 'scale(1.1)'
    }
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest
    })
  },
  expandOpen: {
    transform: 'rotate(180deg)'
  },
  avatar: {
    backgroundColor: red[500]
  }
}));

export default function ProjectCard(props) {
  const classes = useStyles();
  const [expandedId, setExpandedId] = React.useState(-1);

  const handleExpandClick = (i) => {
    // setExpanded(!expanded);
    setExpandedId(expandedId === i ? -1 : i);
  };

  return (
    <>
      {props.projects.map((item, i) => (
        <>
          <div className="col-lg-4 col-sm-12 my-5  ">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <div>
              <div className="mt-5">
                <a href={item.github} target="blank">
                  <button className="btn btn-primary mr-2">Github</button>
                </a>
                <a href={item.deployed} target="blank">
                  <button className="btn btn-primary mr-2">Deployed</button>
                </a>
              </div>
            </div>
          </div>
          <div className="col-lg-8 col-sm-12 my-5 imgContainer">
            <img src={item.image} className="image" />
            <div class="overlay">
              <div class="text">
                <h4>Technologies:</h4>

              </div>
            </div>
          </div>
        </>
